#Install AD DS, DNS and GPMC$featureLogPath = "c:\poshlog\featurelog.txt"start-job -Name addFeature -ScriptBlock {#Add-WindowsFeature -Name "RSAT-AD-Tools" -IncludeAllSubFeature -IncludeManagementToolsAdd-WindowsFeature -Name "ad-domain-services" -IncludeAllSubFeature -IncludeManagementToolsAdd-WindowsFeature -Name "dns" -IncludeAllSubFeature -IncludeManagementToolsAdd-WindowsFeature -Name "gpmc" -IncludeAllSubFeature -IncludeManagementTools }Wait-Job -Name addFeatureGet-WindowsFeature | Where installed >>$featureLogPath# Create New Forest, add Domain Controller$DomainName = "pslab.test"$NetbiosName = "PSLAB"$DRSMpwd = ConvertTo-SecureString -string "1qaz@WSX" -AsPlainText -ForceImport-Module ADDSDeploymentInstall-ADDSForest -CreateDnsDelegation:$false `-DatabasePath "C:\Windows\NTDS" `-LogPath "C:\Windows\NTDS" `-SysvolPath "C:\Windows\SYSVOL" `-ForestMode "Win2012" `-DomainMode "Win2012" `-DomainName $DomainName `-DomainNetbiosName $NetbiosName `-InstallDns:$true `-SafeModeAdministratorPassword $DRSMpwd `-NoRebootOnCompletion:$false `-Force:$true