#install roles and features$featureLogPath = "c:\poshlog\featurelog.txt"New-Item $featureLogPath -ItemType file -Forcestart-job -Name addFeature -ScriptBlock {Add-WindowsFeature -Name "ad-domain-services" -IncludeAllSubFeature -IncludeManagementToolsAdd-WindowsFeature -Name "dns" -IncludeAllSubFeature -IncludeManagementToolsAdd-WindowsFeature -Name "gpmc" -IncludeAllSubFeature -IncludeManagementTools }Wait-Job -Name addFeatureGet-WindowsFeature | Where installed >>$featureLogPath# CreateAdditionalDC.ps1$DomainName = "pslab.test"$NetbiosName = "PSLAB"$User = “$NetbiosName\administrator"$Credpwd = ConvertTo-SecureString -String "1qaz@WSX" -AsPlainText -Force$Cred = New-Object –TypeName System.Management.Automation.PSCredential `  –ArgumentList $User, $Credpwd$DRSMpwd = ConvertTo-SecureString -String "1qaz@WSX" -AsPlainText -ForceImport-Module ADDSDeploymentInstall-ADDSDomainController `-Credential $Cred `-SafeModeAdministratorPassword $DRSMpwd `-DomainName $DomainName `-SiteName "Default-First-Site-Name" `-ReplicationSourceDC "pslab-dc01.pslab.test" `-CriticalReplicationOnly:$false `-DatabasePath "C:\Windows\NTDS" `-LogPath "C:\Windows\NTDS" `-SysvolPath "C:\Windows\SYSVOL" `-InstallDns:$True `-CreateDnsDelegation:$False `-NoGlobalCatalog:$false `-NoRebootOnCompletion:$false `-Force:$true